mapper.update:
UPDATE answer
SET updateDate = NOW(),
`body` = #{body}
WHERE id = #{id}

UPDATE answer
SET choiceStatus = 1
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM answer
WHERE memberId = #{loginedMemberId}
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}

SELECT COUNT(*)
FROM answer
WHERE memberId = #{loginedMemberId}
